import React,{useCallback,useState} from "react";
import AppLayout from "../components/AppLayout";
import Head from 'next/head'
import { Form, Input,Checkbox,Button } from "antd";
import useInput from "../hooks/useInput";
import styled from "styled-components";
import { SIGN_UP_REQUEST } from "../reducers/user";
import { useDispatch, useSelector } from 'react-redux';

const ErrorMessage = styled.div`
    color: red;
`;

const Signup = ()=>{
    const [email,onChangedEmail]=useInput('');
    const [nickname,onChangedNickname]=useInput('');
    const [password,onChangedPassword]=useInput('');

    const [passwordCheck,setPasswordCheck]=useState('');
    const [passwrodError,setPasswordError]=useState(false);
    const onChangePasswordCheck = useCallback((e)=>{
        setPasswordCheck(e.target.value);
        setPasswordError(e.target.value !== password);
    },[password]);

    const dispatch = useDispatch();
    const {signUpLoading}=useSelector((state)=>state.user);

    const [term,setTerm]=useState('');
    const [termError,setTermError]=useState(false);
    const onChangeTerm = useCallback((e)=>{
        setTerm(e.target.checked);
        setTermError(false);
    },[])

    const onSubmit = useCallback(()=>{
        if(password !== passwordCheck){
            return setPasswordError(true);
        }
        if(!term){
            return setTermError(true);
        }
        console.log(email,nickname,password);
        dispatch({
            type:SIGN_UP_REQUEST,
            data:{email,password,nickname}
        })
    },[password,passwordCheck,term]);
    return (
    <AppLayout>
    <Head>
        <title>회원가입 | NodeBird</title>
    </Head>
    <Form onFinish={onSubmit}>
        <div>
            <label htmlFor="user-email">아이디</label>
            <br/>
            <Input name="user-email" type="email" value={email} required onChange={onChangedEmail}/>
        </div>
        <div>
            <label htmlFor="user-nickname">닉네임</label>
            <br/>
            <Input name="user-nickname" value={nickname} required onChange={onChangedNickname}/>
        </div>
        <div>
            <label htmlFor="user-password">비밀번호</label>
            <br/>
            <Input name="user-password" value={password} required onChange={onChangedPassword}/>
        </div>
        <div>
            <label htmlFor="user-password-check">비밀번호 체크</label>
            <br/>
            <Input name="user-password-check" value={passwordCheck} required onChange={onChangePasswordCheck}/>
            {passwrodError && <ErrorMessage>비밀번호가 일치하지않습니다</ErrorMessage>}
        </div>
        <div>
            <Checkbox name="user-term" checked={term} onChange={onChangeTerm}>약관에 동의합니다.</Checkbox>
            {termError && <ErrorMessage>약관에 동의하셔야합니다.</ErrorMessage>}
        </div>
        <div style={{marginTop:10}}>
            <Button type="primary" htmlType="submit" loading={signUpLoading}>가입하기</Button>
        </div>
    </Form>
    </AppLayout>)
}
export default Signup;
