export const initialState = {
    isLoggedIn:false,
    me:null,
    signUpData:{},
    loginData:{},
};

export const loginAction=(data)=>{
    return{
    type:'LOG_IN',
    data,
}
};
export const logoutAction=()=>{
    return{
    type:'LOG_OUT',
}
};

const reducer =(state=initialState,action)=>{
    switch(action.type){
        default:
            return state;
            case 'LOG_IN':
            
            return{
                ...state,
                    ...state,
                    isLoggedIn:true,
                    me:action.data,
            }
            case 'LOG_OUT':
            return{
                ...state,
                    ...state,
                    isLoggedIn:false,
                    me:null,
            }
    }
}

export default reducer;
