export const initialState = {
    isLoggedIn:false, 
    isLoggingIn:false,//로그인 시도중
    isLoggingOut:false, //로그아웃 시도중
    me:null,
    signUpData:{},
    loginData:{},
};

export const loginRequestAction=(data)=>{
    return{
    type:'LOG_IN_REQUEST',
    data,
}
};


export const logoutRequestAction=()=>{
    return{
    type:'LOG_OUT_REQUEST',
}
};

const reducer =(state=initialState,action)=>{
    switch(action.type){
        default:
            return state;
            case 'LOG_IN_REQUEST':
            
            return{

                    ...state,
                    isLoggingIn:true,
            };
            case 'LOG_IN_SUCCESS':
            
            return{

                    ...state,
                    isLoggingIn:false,
                    isLoggedIn:true,
                    me:{...action.data,nickname:'river'},
            };
            case 'LOG_IN_FAILURE':
            
            return{
                ...state,
                    ...state,
                    isLoggingIn:false,
                    isLoggedIn:false,
                    me:action.data,
            };

            case 'LOG_OUT_REQUEST':
            return{
                    ...state,
                    isLoggingOut:true,
                    me:null,
            };
            case 'LOG_OUT_SUCCESS':
            return{
                    ...state,
                    isLoggingOut:false,
                    isLoggedIn:false,
                    me:null,
            };
            case 'LOG_OUT_FAILURE':
            return{
                    ...state,
                    isLoggingOut:false,
            };
    }
}

export default reducer;
