import React,{useState,useCallback} from 'react';
import PropTypes from 'prop-types';
import { Avatar, Button, Card, Popover } from 'antd';
import { RetweetOutlined } from '@ant-design/icons';
import { EllipsisOutlined, HeartOutlined,HeartTwoTone,  MessageOutlined } from '@ant-design/icons';
import { useSelector } from 'react-redux';
import PostImages from './PostImages';

const PostCard=({post})=>{
    const [liked,setLiked]=useState(false);
    const [commnetFormOpened,setCommentFormOpened]=useState(false);
    const onToggleLike =useCallback(()=>{
        setLiked((prev)=>!prev);
    },[]);
    const onToggleComment=useCallback(()=>{
        setCommentFormOpened((prev)=>!prev)
    },[])
    const {id} = useSelector((state)=>state.user.me);
    return(
        <div style={{marginButtom:20}}>
            <Card 
                cover={post.Images[0]&&<PostImages images={post.Images}/>}
                actions={[
                    <RetweetOutlined key="retweet"/>,
                    liked
                    ?<HeartTwoTone twoToneColor="#eb2f96"  key="heart" onClick={onToggleLike}/>
                    :<HeartOutlined key="heart" onClick={onToggleLike}/>,
                    <MessageOutlined key="comment" onClick={onToggleComment}/>,
                    <Popover key="more" content={(
                        <Button.Group>
                           {id && post.User.id === id?(
                           <>
                           <Button>수정</Button>
                            <Button type='danger'>삭제</Button>
                            </>
                            ):<Button>신고</Button>}
                        </Button.Group>
                    )}>
                        <EllipsisOutlined/>
                    </Popover>
                ]}
            >
                <Card.Meta
                avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                title={post.User.nickname}
                description={post.content}
                />
            </Card>
            {commnetFormOpened&&(
                <div>
                   댓글
                </div>
                //  <CommentForm/>
                //     <Commment/>
            )}
        </div>
    )
}

PostCard.propTypes={
    post:PropTypes.shape({
        id:PropTypes.number,
        User:PropTypes.object,
        content:PropTypes.string,
        createdAt:PropTypes.object,
        Comments:PropTypes.arrayOf(PropTypes.object),
        Images:PropTypes.arrayOf(PropTypes.object),
    }).isRequired,
}

export default PostCard;
